func (cdgb *CodingDraftGroupBy) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range cdgb.fields {
		if !codingdraft.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for group-by", f)}
		}
	}
	selector := cdgb.sqlQuery()
	if err := selector.Err(); err != nil {
		return err
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := cdgb.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (cdgb *CodingDraftGroupBy) sqlQuery() *sql.Selector {
	selector := cdgb.sql.Select()
	aggregation := make([]string, 0, len(cdgb.fns))
	for _, fn := range cdgb.fns {
		aggregation = append(aggregation, fn(selector))
	}
	// If no columns were selected in a custom aggregation function, the default
	// selection is the fields used for "group-by", and the aggregation functions.
	if len(selector.SelectedColumns()) == 0 {
		columns := make([]string, 0, len(cdgb.fields)+len(cdgb.fns))
		for _, f := range cdgb.fields {
			columns = append(columns, selector.C(f))
		}
		columns = append(columns, aggregation...)
		selector.Select(columns...)
	}
	return selector.GroupBy(selector.Columns(cdgb.fields...)...)
}

// CodingDraftSelect is the builder for selecting fields of CodingDraft entities.
type CodingDraftSelect struct {
	*CodingDraftQuery
	// intermediate query (i.e. traversal path).
	sql *sql.Selector
}

// Scan applies the selector query and scans the result into the given value.
func (cds *CodingDraftSelect) Scan(ctx context.Context, v interface{}) error {
	if err := cds.prepareQuery(ctx); err != nil {
		return err
	}
	cds.sql = cds.CodingDraftQuery.sqlQuery(ctx)
	return cds.sqlScan(ctx, v)
}

